import React, { useState } from 'react';
import Login from './Login';
import AdminDashboard from './AdminDashboard';
import UserDashboard from './UserDashboard';
import GraphVisualization from './GraphVisualization';
import { GetTrainRoutes } from './TrainRouteService';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [viewAllRoutes, setViewAllRoutes] = useState(false);
  const [routes, setRoutes] = useState([]);

  const handleLogin = (isAdmin) => {
    setIsAuthenticated(true);
    setIsAdmin(isAdmin);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsAdmin(false);
  };

  const handleViewAllRoutes = async () => {
    try {
      const allRoutes = await GetTrainRoutes();
      setRoutes(allRoutes);
      setViewAllRoutes(true);
    } catch (error) {
      console.error('Error viewing all routes:', error);
    }
  };

  return (
    <div className="App">
      {!isAuthenticated ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <button onClick={handleLogout}>Logout</button>
          {isAdmin ? (
            <AdminDashboard />
          ) : (
            <UserDashboard />
          )}
          <button onClick={handleViewAllRoutes}>View All Routes</button>
          {viewAllRoutes && (
            <GraphVisualization routes={routes} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
