export async function GetTrainRoutes() {
  try {
    const response = await fetch('http://localhost:5006/api/routes');
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching train routes:', error);
    return [];
  }
}

export async function AddTrainRoute(route) {
  try {
    const response = await fetch('http://localhost:5006/api/routes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(route),
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error adding train route:', error);
    throw error;
  }
}

export async function DeleteTrainRoute(routeId) {
  try {
    const response = await fetch(`http://localhost:5006/api/routes/${routeId}`, {
      method: 'DELETE',
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error deleting train route:', error);
    throw error;
  }
}

export async function UpdateTrainRoute(route) {
  try {
    const response = await fetch(`http://localhost:5006/api/routes/${route.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(route),
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error updating train route:', error);
    throw error;
  }
}
